import React from 'react'

export default function TextWithMarks({ node }) {
  const { text, bold, italic, underline, children, type } = node
  if (type === 'mention') {
    return node.children.map((c, i) => <React.Fragment key={i}>{c.text}</React.Fragment>)
  }
  let content = text != null ? text : children.map((c, i) => <TextWithMarks key={i} node={c} />)
  if (bold) content = <strong>{content}</strong>
  if (italic) content = <em>{content}</em>
  if (underline) content = <u>{content}</u>
  return <>{content}</>
}
