import React from 'react'
import TextWithMarks from './TextWithMarks'

export default function Mention({ node }) {
  return (
    <span className="mention" style={{ backgroundColor: node.color }}>
      {node.children.map((child, i) => <TextWithMarks key={i} node={child} />)}
    </span>
  )
}
