import React, { useState, useEffect } from 'react'
import data from './data/input.json'
import BlockRenderer from './components/BlockRenderer'

function assignClauseNumbers(nodes) {
  let counter = 1
  function traverse(items) {
    return items.map(item => {
      if (item.type === 'clause') {
        const newItem = { ...item, clauseNumber: counter++ }
        if (item.children) newItem.children = traverse(item.children)
        return newItem
      }
      if (item.children) {
        return { ...item, children: traverse(item.children) }
      }
      return item
    })
  }
  return traverse(nodes)
}

export default function App() {
  const [nodes, setNodes] = useState([])

  useEffect(() => {
    setNodes(assignClauseNumbers(data))
  }, [])

  return (
    <div className="container">
      {nodes[0].children.map((node, index) => (
        <BlockRenderer key={index} node={node} />
      ))}
    </div>
  )
}
